contracts/bettingPoolFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "hardhat/console.sol";
import {BettingPool} from "./bettingPool.sol";
import {notQuiteRandomCoinFlip} from "./notQuiteRandom/coinFlip.sol";
import {fakeYieldSource} from "./test/fakeYield.sol";


contract BettingPoolFactory { // TODO: use ownable?
  BettingPool[] public pools;

  event PoolCreated(
    address indexed owner,
    address indexed token,
    address indexed resultController,
    address pollAddress,
    uint256 poolIndex
  );

  function createPool (address token, address resultController, address yieldSrc) external returns(uint256) {
    return _createPool(token, resultController, yieldSrc);
  }

  function createDefaultPool (address token) external returns(uint256) {
    notQuiteRandomCoinFlip controller = new notQuiteRandomCoinFlip(msg.sender);
    fakeYieldSource yieldSource = new fakeYieldSource(token);
    return _createPool (token, address(controller), address(yieldSource));
  }

  function poolCount () public view returns(uint256) {
    return pools.length;
  }

  function _createPool (address token, address resultController, address yieldSrc) private returns(uint256) {
    BettingPool pool = new BettingPool(msg.sender, token, resultController, yieldSrc);
    pools.push(pool);
    uint256 pollIndex = pools.length - 1;
    emit PoolCreated(msg.sender, address(token), address(resultController), address(pool), pollIndex);
    return pollIndex;
  }
}

contracts/notQuiteRandom/coinFlip.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import {IResultController} from "../IResultController.sol";
import {NotQuiteRandom} from "./NotQuiteRandom.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

import "hardhat/console.sol";

contract notQuiteRandomCoinFlip is IResultController, NotQuiteRandom, Ownable {
  bool private _hasResult;
  uint256 result;
  uint256 optionsCount;
  mapping(uint256 => bytes32) names;

  constructor (address owner) {
    _hasResult = false;
    optionsCount = 2;
    names[0] = bytes32(abi.encodePacked("Heads"));
    names[1] = bytes32(abi.encodePacked("Tails"));
    transferOwnership(owner);
  }

  function generateResult () external onlyOwner {
    require(!_hasResult, "Already has result");
    _hasResult = true;
    result = getRandomNumberFromInterval(2);
    emit resultGenerated (address(this), result);
  }

  function hasResult () external view override returns (bool)  {
    return _hasResult;
  }

  function getResult () external view override returns (uint256) {
    return result;
  }

  function getOptionsCount () external view override returns (uint256) {
    return optionsCount;
  }

  function getOptionName (uint256 index) external view override returns (bytes32) {
    return names[index];
  }
}

contracts/test/fakeYield.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import {IYieldSource} from "../IYieldSource.sol";
import {MyToken} from "./token.sol";
import "hardhat/console.sol";

contract fakeYieldSource is IYieldSource {
  MyToken token;
  uint256 amountDeposited;
  uint256 amountWithdrawn;
  constructor (address _token) {
    token = MyToken(_token);
    amountDeposited = 0;
    amountWithdrawn = 0;
  }

  function deposit (uint256 amount) external override {
    amountDeposited += amount;
    token.transferFrom(msg.sender, address(this), amount);
    // generate fake 10% yield
    token.mint(address(this), (110 * amount) / 100 - amount);
  }

  function getBalance () external view override returns (uint256) {
    return token.balanceOf(address(this));
  }

  function getYield () external view override returns (uint256) {
    return token.balanceOf(address(this)) + amountWithdrawn - amountDeposited;
  }

  function withdraw (address to, uint256 amount) external override {
    amountWithdrawn += amount;
    token.transfer(to, amount);
  }
}

contracts/notQuiteRandom/NotQuiteRandom..content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.7.0 <0.9.0;

contract NotQuiteRandom {
    function getRandomNumber()
        public
        view
        returns (uint256 notQuiteRandomNumber)
    {
        notQuiteRandomNumber = uint256(blockhash(block.number - 1));
    }

    function getRandomNumberFromInterval(uint256 limit) public view returns (uint256 heads) {
        return getRandomNumber() % limit;
    }
}

